u0447\u0443\u0432\u0441\u0442\u0432\u0443\u0435\u0442\u0435 \u0436\u0430\u0436\u0434\u0443, \u0442\u043e \u043b\u0443\u0447\u0448\u0435 \u0432\u044b\u043f\u0438\u0442\u044c \u043d\u0435\u043c\u043d\u043e\u0433\u043e \u0431\u043e\u043b\u044c\u0448\u0435. \u041e\u0440\u0433\u0430\u043d\u0438\u0437\u043c \u0441\u0430\u043c \u043f\u043e\u0441\u044b\u043b\u0430\u0435\u0442 \u043d\u0430\u043c \u0441\u0438\u0433\u043d\u0430\u043b\u044b, \u043a\u043e\u0433\u0434\u0430 \u043d\u0443\u0436\u043d\u043e \u043f\u043e\u043f\u043e\u043b\u043d\u0438\u0442\u044c \u0437\u0430\u043f\u0430\u0441\u044b. \u041d\u0435 \u0441\u043b\u0435\u0434\u0443\u0435\u0442 \u0437\u0430\u043c\u0435\u043d\u044f\u0442\u044c \u043e\u0431\u044b\u0447\u043d\u0443\u044e \u0447\u0438\u0441\u0442\u0443\u044e \u0432\u043e\u0434\u0443 \u0434\u0440\u0443\u0433\u0438\u043c\u0438 \u0436\u0438\u0434\u043a\u043e\u0441\u0442\u044f\u043c\u0438, \u0432\u0440\u043e\u0434\u0435 \u0447\u0430\u044f \u0438\u043b\u0438 \u0441\u043e\u043a\u043e\u0432. \u041a\u043e\u043d\u0435\u0447\u043d\u043e, \u0443\u043f\u043e\u0442\u0440\u0435\u0431\u043b\u044f\u0442\u044c \u0438\u0445 \u0442\u043e\u0436\u0435 \u043c\u043e\u0436\u043d\u043e, \u043d\u043e \u0431\u043e\u043b\u044c\u0448\u0443\u044e \u0447\u0430\u0441\u0442\u044c \u0440\u0430\u0446\u0438\u043e\u043d\u0430 \u0434\u043e\u043b\u0436\u043d\u0430 \u0441\u043e\u0441\u0442\u0430\u0432\u043b\u044f\u0442\u044c \u0438\u043c\u0435\u043d\u043d\u043e \u0432\u043e\u0434\u0430. \u041e\u0442 \u0435\u0435 \u043a\u0430\u0447\u0435\u0441\u0442\u0432\u0430 \u0442\u0430\u043a\u0436\u0435 \u0437\u0430\u0432\u0438\u0441\u0438\u0442 \u043c\u043d\u043e\u0433\u043e\u0435. \u041d\u0430\u043f\u0440\u0438\u043c\u0435\u0440, \u0436\u0435\u0441\u0442\u043a\u0430\u044f \u0432\u043e\u0434\u0430, \u043a\u043e\u0442\u043e\u0440\u0430\u044f \u043f\u043e\u043b\u043d\u0430 \u0441\u043e\u043b\u0435\u0439 \u043a\u0430\u043b\u044c\u0446\u0438\u044f, \u0441\u043f\u043e\u0441\u043e\u0431\u043d\u0430 \u0432\u0441\u0435\u0433\u043e \u0437\u0430 \u043f\u0430\u0440\u0443 \u043d\u0435\u0434\u0435\u043b\u044c \u0443\u0431\u0438\u0442\u044c \u0432\u0430\u0448\u0438 \u043f\u043e\u0447\u043a\u0438.<br><br><strong>\u0412\u043b\u0438\u044f\u043d\u0438\u0435 \u043a\u043e\u0444\u0435 \u043d\u0430 \u0432\u0430\u0441 \u0438 \u0432\u0430\u0448 \u0440\u0430\u0441\u043f\u043e\u0440\u044f\u0434\u043e\u043a \u0434\u043d\u044f<br><\/strong><br>\u041c\u043d\u043e\u0433\u0438\u0435 \u043d\u0435 \u043c\u043e\u0433\u0443\u0442 \u043f\u0440\u0435\u0434\u0441\u0442\u0430\u0432\u0438\u0442\u044c \u0441\u0432\u043e\u0435 \u0443\u0442\u0440\u043e \u0431\u0435\u0437 \u0447\u0430\u0448\u0435\u0447\u043a\u0438 \u0433\u043e\u0440\u044f\u0447\u0435\u0433\u043e \u043a\u043e\u0444\u0435. \u0413\u043b\u0430\u0432\u043d\u043e\u0435 \u0432\u0435\u0449\u0435\u0441\u0442\u0432\u043e \u0435\u0433\u043e - \u044d\u0442\u043e \u043a\u043e\u0444\u0435\u0438\u043d. \u041e\u043d \u0441\u043d\u0438\u0436\u0430\u0435\u0442 \u0447\u0443\u0432\u0441\u0442\u0432\u043e \u0443\u0441\u0442\u0430\u043b\u043e\u0441\u0442\u0438 \u043f\u0443\u0442\u0435\u043c \u0431\u043b\u043e\u043a\u0438\u0440\u043e\u0432\u043a\u0438 \u0441\u043f\u0435\u0446\u0438\u0430\u043b\u044c\u043d\u044b\u0445 \u0440\u0435\u0446\u0435\u043f\u0442\u043e\u0440\u043e\u0432 \u0432 \u0446\u0435\u043d\u0442\u0440\u0430\u043b\u044c\u043d\u043e\u0439 \u043d\u0435\u0440\u0432\u043d\u043e\u0439 \u0441\u0438\u0441\u0442\u0435\u043c\u0435. \u041d\u0435\u0439\u0440\u043e\u043c\u0435\u0434\u0438\u0430\u0442\u043e\u0440 \u0443\u0441\u0442\u0430\u043b\u043e\u0441\u0442\u0438 \u0430\u0434\u0435\u043d\u043e\u0437\u0438\u043d \u043d\u0435 \u0434\u043e\u0441\u0442\u0438\u0433\u0430\u0435\u0442 \u0438\u0445. \u0412 \u0440\u0435\u0437\u0443\u043b\u044c\u0442\u0430\u0442\u0435, \u0447\u0435\u043b\u043e\u0432\u0435\u043a \u0447\u0443\u0432\u0441\u0442\u0432\u0443\u0435\u0442 \u0441\u0435\u0431\u044f \u0431\u043e\u043b\u0435\u0435 \u0431\u043e\u0434\u0440\u044b\u043c. \u0423 \u043d\u0435\u